SET FOREIGN_KEY_CHECKS = 0;
DROP TABLE IF EXISTS Genres;
DROP TABLE IF EXISTS Movies;
DROP TABLE IF EXISTS Actors;
DROP TABLE IF EXISTS Movies_Actors;
DROP TABLE IF EXISTS OscarWinners;
SET FOREIGN_KEY_CHECKS = 1;


CREATE TABLE Genres (
	genre varchar(255) NOT NULL,
	PRIMARY KEY (genre),
	UNIQUE (genre)
);



CREATE TABLE Movies (
	movieID int(11) NOT NULL AUTO_INCREMENT,
	movieTitle varchar(255) NOT NULL,
	releaseYear int(11) NOT NULL,
	runtime int(11) NOT NULL,
	ratingIMDB decimal(2, 1),
	ratingRottenTomatoes int(11),
	genre varchar(255),
	PRIMARY KEY (movieID),
	FOREIGN KEY (genre) REFERENCES Genres(genre)
);



CREATE TABLE Actors (
	actorID int(11) NOT NULL AUTO_INCREMENT,
	actorLastName varchar(255) NOT NULL,
	actorFirstName varchar(255) NOT NULL,
	PRIMARY KEY (actorID)
);



CREATE TABLE Movies_Actors (
	movieID int(11) NOT NULL,
	actorID int(11) NOT NULL,
	PRIMARY KEY (movieID, actorID),
	FOREIGN KEY (movieID) REFERENCES Movies(movieID),
	FOREIGN KEY (actorID) REFERENCES Actors(actorID)
);



CREATE TABLE OscarWinners (
	year int(11) NOT NULL,
	bestPicture int(11),
	leadActor int(11),
	leadActress int(11),
	PRIMARY KEY (year),
	UNIQUE (year),
	FOREIGN KEY (bestPicture) REFERENCES Movies(movieID),
	FOREIGN KEY (leadActor) REFERENCES Actors(actorID),
    FOREIGN KEY (leadActress) REFERENCES Actors(actorID)
);

